'A.MODIFIED_BY,',
'A.MODIFIED_DATE,',
'B.AREA_CODE,',
'B.LIST_TITLE,',
'ID,',
'--UPLOAD_ID,',
'v.CABLENUM as CABLE_INSTALL_CABLENUM,',
'INSTALLER_CODE,',
'INSTALL_STATUS,',
'INSTALL_DATE,',
'INSTALL_USER,',
'VERIFY_SOURCE,',
'VERIFY_SOURCE_DT,',
'VERIFY_SOURCE_USER,',
'VERIFY_DEST,',
'VERIFY_DEST_DT,',
