'x.CABLENUM as extra_cablenum,',
'x.area_code as extra_area_code,',
'x.phase,',
'x.SECTOR_AREA_SOURCE,',
'x.sector_area_dest,',
'x.BEAMLINE_AREA,',
'x.SECTOR_GROUP,',
'x.PENETRATION,',
'x.PENETRATION_II,',
'--x.PROJECT,',
