'select * from (',
'select    DISTINCT       ',
'    to_char(sysdate)  today,',
'    cable_listing.area_code, is_number,',
'    cableinv.list_no, list_title, jobnum,',
'    cableinv.system,',
'    cableinv.func,',
'    cableinv.cabletype,',
'    cableinv.cablenum,',
'    toconninv.loc ||''-''|| toconninv.rack ||',
'    DECODE(toconninv.ele,''0'','''',',
'    ''1'',''01'',''2'',''02'',''3'',''03'',''4'',''04'',''5'',''05'',',
'    ''6'',''06'',''7'',''07'',''8'',''08'',''9'',''09'', toconninv.ele) ||',
