'PENETRATION_II',
'from',
'    captar.area,',
'(select * from ',
'(select a.cablenum origin_cablenum, ',
'    a.conninv_id origin_conninv_id, ',
'    b.loc origin_loc, b.rack origin_rack,',
'    DECODE(b.ele,''0'','''',',
'    ''1'',''01'',''2'',''02'',''3'',''03'',''4'',''04'',''5'',''05'',',
'    ''6'',''06'',''7'',''07'',''8'',''08'',''9'',''09'', b.ele) origin_ele,',
'    DECODE(b.side,''B'',''B'',''S'',''S'',''T'',''T'') origin_side,',
'    DECODE(b.slot,''-'','''',to_char(b.slot)) origin_slot,',
'    b.connnum origin_connnum, ',
'    b.pinlist origin_pinlist,',
'--    ''''''''||b.pinlist||'''''''' origin_pinlist,',
'    b.station origin_station,',
'    b.conntype origin_conntype,',
'    b.instr origin_instr',
'from ',
'  (select conninv_id, cablenum,',
'decode(ROW_NUMBER() OVER (PARTITION BY cablenum ORDER BY conninv_id),1,''ORIGIN'',2,''DESTINATION'') type',
'--decode(mod(rownum,2),0,''ORIGIN'',''DESTINATION'') type',
'  from (',
'    select * from captar.conninv',
'    where  cablenum is not null',
'    order by cablenum, conninv_id',
'    )) a, ',
'  conninv b',
'where a.cablenum = b.cablenum and a.conninv_id = b.conninv_id and a.type = ''ORIGIN'') o,',
'(select x.cablenum dest_cablenum, ',
'    x.conninv_id dest_conninv_id,',
'    y.loc dest_loc, y.rack dest_rack,',
'    DECODE(y.ele,''0'','''',',
'    ''1'',''01'',''2'',''02'',''3'',''03'',''4'',''04'',''5'',''05'',',
'    ''6'',''06'',''7'',''07'',''8'',''08'',''9'',''09'', y.ele) dest_ele,',
'    DECODE(y.side,''B'',''B'',''S'',''S'',''T'',''T'') dest_side,',
'    DECODE(y.slot,''-'','''',to_char(y.slot)) dest_slot,',
'    y.connnum dest_connnum, ',
'    y.pinlist dest_pinlist,',
'    y.station dest_station,',
'    y.conntype dest_conntype,',
'    y.instr dest_instr',
'from ',
'  (select conninv_id, cablenum,',
'decode(ROW_NUMBER() OVER (PARTITION BY cablenum ORDER BY conninv_id),1,''ORIGIN'',2,''DESTINATION'') type',
'--  decode(mod(rownum,2),0,''ORIGIN'',''DESTINATION'') type',
'  from (',
'    select * from captar.conninv',
'    where  cablenum is not null',
'    order by cablenum, conninv_id',
'    )) x, ',
'  conninv y',
'where x.cablenum = y.cablenum and x.conninv_id = y.conninv_id and x.type = ''DESTINATION'') d',
'where o.origin_cablenum = d.dest_cablenum) conninv,',
