,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'select today, area_code, jobnum, system, func, cabletype, cablenum,',
'origin_loc, origin_rack, origin_conntype,',
'--origin_location,',
'case ',
' when length(trim(origin_location)) > 20',
' then substr(origin_location,1,20)||''#''',
' else origin_location',
'end as origin_location,',
'destination_loc, destination_rack, destination_conntype,',
'--destination_location, length(destination_location),',
'case ',
' when length(trim(destination_location)) > 20',
' then substr(destination_location,1,20)||''#''',
' else destination_location',
'end as destination_location',
'from (',
'select',
'    to_char(sysdate)  today,',
'    cable_listing.area_code,',
'    jobnum,',
'    cableinv.system, ',
'    cableinv.func, ',
'    cableinv.cabletype, ',
'    cableinv.cablenum, ',
'    conninv.*',
'from',
'    captar.area,',
'(select * from ',
'(select a.cablenum origin_cablenum, ',
'    a.conninv_id origin_conninv_id, ',
'    b.loc origin_loc, b.rack origin_rack, b.conntype origin_conntype,',
'    b.loc ||''-''|| b.rack ||',
'    substr((DECODE(b.ele,''0'','''',',
'    ''1'',''01'',''2'',''02'',''3'',''03'',''4'',''04'',''5'',''05'',',
'    ''6'',''06'',''7'',''07'',''8'',''08'',''9'',''09'', b.ele) ||',
'    DECODE(b.side,''B'',''B'',''S'',''S'',''T'',''T'') ||''-''||',
'    DECODE(b.slot,''-'','''',to_char(b.slot) ||''--'') ||',
'    b.connnum ||'' ''|| b.station),1,20) origin_location',
'from ',
'  (select conninv_id, cablenum,',
'decode(ROW_NUMBER() OVER (PARTITION BY cablenum ORDER BY conninv_id),1,''ORIGIN'',2,''DESTINATION'') type',
'--  decode(mod(rownum,2),0,''DESTINATION'',''ORIGIN'') type',
'  from (',
'    select * from captar.conninv',
'    where  cablenum is not null',
'    order by cablenum, conninv_id',
'    )) a, ',
'  conninv b',
'where a.cablenum = b.cablenum and a.conninv_id = b.conninv_id and a.type = ''ORIGIN'') o,',
'(select x.cablenum destination_cablenum, ',
'    x.conninv_id destination_conninv_id,',
'    y.loc destination_loc, y.rack destination_rack, y.conntype destination_conntype,',
'    y.loc ||''-''|| y.rack ||',
'    DECODE(y.ele,''0'','''',',
'    ''1'',''01'',''2'',''02'',''3'',''03'',''4'',''04'',''5'',''05'',',
'    ''6'',''06'',''7'',''07'',''8'',''08'',''9'',''09'', y.ele) ||',
'    DECODE(y.side,''B'',''B'',''S'',''S'',''T'',''T'') ||''-''||',
'    DECODE(y.slot,''-'','''',y.slot ||''-'') ||',
'    y.connnum ||'' ''|| y.station destination_location',
'from ',
'  (select conninv_id, cablenum,',
'decode(ROW_NUMBER() OVER (PARTITION BY cablenum ORDER BY conninv_id),1,''ORIGIN'',2,''DESTINATION'') type',
'--  decode(mod(rownum,2),0,''DESTINATION'',''ORIGIN'') type',
'  from (',
'    select * from captar.conninv',
'    where  cablenum is not null',
'    order by cablenum, conninv_id',
'    )) x, ',
'  conninv y',
'where x.cablenum = y.cablenum and x.conninv_id = y.conninv_id and x.type = ''DESTINATION'') d',
'where o.origin_cablenum = d.destination_cablenum) conninv,',
'    captar.cable_listing,',
'    captar.cableinv',
'where',
'    cable_listing.area_code = area.area_code',
'and cableinv.list_no = cable_listing.list_no',
'and cableinv.cablenum = conninv.origin_cablenum',
'and cableinv.cablenum = conninv.destination_cablenum',
'and :P54_AREA_CODE is not null',
'and (:P54_JOBNUM is NULL or jobnum = upper(:P54_JOBNUM)) ',
'--and (cable_listing.area_code = :P40_AREA_CODE or :P40_AREA_CODE is null)',
'--and (:P40_AREA_CODE || :P40_REPORT_SEARCH || :P40_JOBNUM) IS NOT NULL)',
'UNION ALL',
'select',
'    to_char(sysdate)  today,',
'    cable_listing.area_code,',
'    jobnum,',
'    cableinv.system, ',
'    cableinv.func, ',
'    cableinv.cabletype, ',
'    cableinv.cablenum, ',
'    conninv.*',
'from',
'    captar.area,',
'(select * from ',
'(select a.cablenum origin_cablenum, ',
'    a.conninv_id origin_conninv_id, ',
'    b.loc origin_loc, b.rack origin_rack, b.conntype origin_conntype,',
'    b.loc ||''-''|| b.rack ||',
'    substr((DECODE(b.ele,''0'','''',',
'    ''1'',''01'',''2'',''02'',''3'',''03'',''4'',''04'',''5'',''05'',',
'    ''6'',''06'',''7'',''07'',''8'',''08'',''9'',''09'', b.ele) ||',
'    DECODE(b.side,''B'',''B'',''S'',''S'',''T'',''T'') ||''-''||',
'    DECODE(b.slot,''-'','''',to_char(b.slot) ||''--'') ||',
'    b.connnum ||'' ''|| b.station),1,20) origin_location',
'from ',
'  (select conninv_id, cablenum,',
'decode(ROW_NUMBER() OVER (PARTITION BY cablenum ORDER BY conninv_id),1,''ORIGIN'',2,''DESTINATION'') type',
'--  decode(mod(rownum,2),0,''DESTINATION'',''ORIGIN'') type',
'  from (',
'    select * from captar.conninv',
'    where  cablenum is not null',
'    order by cablenum, conninv_id',
'    )) a, ',
'  conninv b',
'where a.cablenum = b.cablenum and a.conninv_id = b.conninv_id and a.type = ''ORIGIN'') o,',
'(select x.cablenum destination_cablenum, ',
'    x.conninv_id destination_conninv_id,',
'    y.loc destination_loc, y.rack destination_rack, y.conntype destination_conntype,',
'    y.loc ||''-''|| y.rack ||',
'    DECODE(y.ele,''0'','''',',
'    ''1'',''01'',''2'',''02'',''3'',''03'',''4'',''04'',''5'',''05'',',
'    ''6'',''06'',''7'',''07'',''8'',''08'',''9'',''09'', y.ele) ||',
'    DECODE(y.side,''B'',''B'',''S'',''S'',''T'',''T'') ||''-''||',
'    DECODE(y.slot,''-'','''',y.slot ||''-'') ||',
'    y.connnum ||'' ''|| y.station destination_location',
'from ',
'  (select conninv_id, cablenum,',
'decode(ROW_NUMBER() OVER (PARTITION BY cablenum ORDER BY conninv_id),1,''ORIGIN'',2,''DESTINATION'') type',
'--  decode(mod(rownum,2),0,''DESTINATION'',''ORIGIN'') type',
'  from (',
'    select * from captar.conninv',
'    where  cablenum is not null',
'    order by cablenum, conninv_id',
'    )) x, ',
'  conninv y',
'where x.cablenum = y.cablenum and x.conninv_id = y.conninv_id and x.type = ''DESTINATION'') d',
'where o.origin_cablenum = d.destination_cablenum) conninv,',
'    captar.cable_listing,',
'    captar.cableinv',
'where',
'    cable_listing.area_code = area.area_code',
'and cableinv.list_no = cable_listing.list_no',
'and cableinv.cablenum = conninv.origin_cablenum',
'and cableinv.cablenum = conninv.destination_cablenum',
'and :P54_AREA_CODE is not null',
'and (:P54_JOBNUM is NULL or jobnum = upper(:P54_JOBNUM)))',
'where (',
' instr(upper(area_code),upper(nvl(:P54_REPORT_SEARCH,area_code))) > 0 or',
' instr(upper(jobnum),upper(nvl(:P54_REPORT_SEARCH,jobnum))) > 0 or',
' instr(upper(system),upper(nvl(:P54_REPORT_SEARCH,system))) > 0 or',
' instr(upper(func),upper(nvl(:P54_REPORT_SEARCH,func))) > 0 or',
