,p_query_type=>'SQL'
,p_plug_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'with v as (',
'select a.UPLOAD_ID, ',
'a.DATE_UPLOADED,',
'a.UPLOADED_BY,',
'a.COMMENTS,',
'a.filename, ',
