'from CABLE_TRACKIT_UPLOAD_LOG a,',
'(select upload_id, count(*) total_lines from CABLE_TRACKIT_UPLOAD group by upload_id) ln',
'where a.upload_id = ln.upload_id(+)),',
'k as (',
'select a.upload_id error_upload_id,  ',
'sum(case when a.action=''ERROR'' then 1 else 0 end) as error_count,',
'sum(case when a.action!=''ERROR'' then 1 else 0 end) as insert_count',
'from CABLE_INSTALL_UPLOAD_ERROR a',
'group by a.upload_id)',
'select v.upload_id, ',
'v.date_uploaded,',
'v.uploaded_by,',
'v.COMMENTS,',
'v.filename, ',
'v.total_lines,',
'k.error_upload_id,',
'k.error_count,',
'k.insert_count',
'from v,k',
'where v.upload_id = k.error_upload_id (+)'))
