prompt --application/shared_components/logic/application_processes/post_auth_setup
begin
wwv_flow_api.create_flow_process(
 p_id=>wwv_flow_api.id(433144097626336308)
,p_process_sequence=>1
,p_process_point=>'AFTER_LOGIN'
,p_process_type=>'NATIVE_PLSQL'
,p_process_name=>'Post_Auth_Setup'
,p_process_sql_clob=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'  v_user_role VARCHAR2(50);',
'  v_user_role_id   NUMBER;',
' cursor c1 is',
'  select u.role, ur.role_id',
'   from ssrl_rsw_user_roles ur',
'       ,ssrl_rsw_roles u',
'  where ur.user_id = :P0_PERSON_ID',
'  and   u.role_id = ur.role_id',
'  and   ur.STATUS_AI_CHK = ''A'';',
'',
'begin',
'--APEX_DEBUG.ENABLE (9);',
'--apex_debug.message(''Inside Post_Auth_Setup, the value of APP_USER= %s'', :APP_USER);',
'--',
'begin',
'  select max(but_sid) into :P0_PERSON_ID',
'  from but',
'  where but_kid = upper(v(''APP_USER''));',
'',
'--apex_debug.message(''Inside Post_Auth_Setup, the value of max(but_sid) P0_PERSON_ID= %s'', :P0_PERSON_ID);',
'end;',
'',
'begin',
'',
'  select name, maildisp into :P0_MYNAME, :P0_MYEMAIL',
'  from person',
'  where key = :P0_PERSON_ID;',
'',
'  :P0_NAME := :P0_MYNAME;',
'exception ',
'  when others then ',
'    :P0_MYNAME := ''Unknown'';',
'    :P0_NAME := ''Unknown'';',
'    :P0_MYEMAIL := null; ',
'end;',
'--',
'begin',
'  FOR c1_rec in c1 LOOP',
'   v_user_role := v_user_role||'':''||c1_rec.role;',
'   v_user_role_id := c1_rec.role_id;',
'apex_debug.message(''Inside Post_Auth_Setup, the value of P101_USERNAME= %s'', :P101_USERNAME);',
'apex_debug.message(''Inside Post_Auth_Setup, the value of 1 v_user_role= %s'', v_user_role);',
'  --',
'insert into ssrl_rsw_user_log',
'(username',
',user_sid',
',role_id',
',created_date',
')',
'values',
'(:P101_USERNAME',
',:P0_PERSON_ID',
',v_user_role_id',
',sysdate',
');',
'',
'  END LOOP;',
'apex_debug.message(''Inside Post_Auth_Setup, the value of 2 v_user_role= %s'', v_user_role);',
'  IF substr(v_user_role,1,1) = '':'' THEN',
'   v_user_role := substr(v_user_role,2);',
'  END IF;',
'apex_debug.message(''Inside Post_Auth_Setup, the value of 3 v_user_role= %s'', v_user_role);',
' :P0_USER_ROLE := v_user_role;',
'end;',
'',
'end;'))
);
end;
