prompt --application/shared_components/security/authentications/dynamic_webauth_ldap_auth
begin
wwv_flow_api.create_authentication(
 p_id=>wwv_flow_api.id(352490574923643982)
,p_name=>'Dynamic_WebAuth_LDAP_Auth'
,p_scheme_type=>'NATIVE_CUSTOM'
,p_attribute_01=>'do_sentry '
,p_attribute_03=>'do_authenticate'
,p_attribute_05=>'Y'
,p_attribute_09=>'NO_SSL'
,p_attribute_11=>'Y'
,p_attribute_14=>'STD'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'function do_sentry ',
'(p_username varchar2 default null, p_password varchar2 default null) ',
'return boolean is',
'host_addr varchar2(200);',
'flag number := 0;',
'host_addr1 varchar2(200);',
'flag1 number := 0;',
'remote_addr varchar2(200);',
'ip_addr varchar2(200);',
'',
'begin',
'--APEX_DEBUG.DISABLE;',
'--APEX_DEBUG.ENABLE (9);',
'--apex_debug.message(''In do_sentry the value of APP_USER= %s'', :APP_USER);',
'',
'    -- See if we are already authenticated, if so then just return true',
'    IF :APP_USER IS NOT NULL AND :APP_USER != ''nobody''',
'    THEN ',
'        RETURN TRUE;',
'    END IF;',
'',
'    -- We are going to compare against IP address, so find out the client''s IP',
'    -- Made changes in getting the IP Address from X-FORWARDED_FOR. Refer to Cater #142399',
'',
'   remote_addr := owa_util.get_cgi_env(''REMOTE_ADDR'');',
'--    host_addr1 := substr(owa_util.get_cgi_env(''REMOTE_ADDR''),1,10);',
'    ip_addr := owa_util.get_cgi_env(''X-FORWARDED-FOR'');',
'',
'host_addr := nvl(ip_addr, remote_addr );',
'host_addr1 := substr(host_addr , 1,10);',
'',
'-- apex_debug.message(''remote_addr = %s'', remote_addr );',
'-- apex_debug.message(''ip_addr = %s'', ip_addr );',
'-- apex_debug.message(''host_addr = %s'', host_addr );',
'-- apex_debug.message(''host_addr1 = %s'', host_addr1 );',
'',
'   -- Test the client IP against a list of IP addresses that need to be ',
'    -- restricted to LDAP authentication',
'-- LCLS/FACET workstations : 134.79.151.23',
'-- SPEAR Workstations : 134.79.46.119, 134.79.46.121, 134.79.46.120',
'-- Poonam''s desktop - 134.79.78.57 (for testing purposes)',
'    select ',
'        decode(host_addr,''134.79.151.23'',1, ''134.79.188.63'',1, ''134.79.188.64'',1, ''134.79.188.65'',1, ''134.79.188.66'',1, ''134.79.188.39'',1, 0) ',
'        into flag ',
'        from dual;',
'     select',
'         decode(host_addr1,''134.79.46.'',1, 0) ',
'        into flag1',
'        from dual;',
'--apex_debug.message(''the value of flag = %s, flag1= %s'', flag,flag1);',
'',
'    -- If we have a restricted IP address then abort the WebAuth authentication step',
'    -- Which will bring up the login page as defined in session not valid below',
'    If (flag = 1 or flag1 = 1) THEN ',
'        return false;  ',
'    ELSE',
'--        :P1_IP_ADDRESS := host_addr;',
'--        :P1_AUTHENTICATION_TYPE := ''WebAuth'';',
'        RETURN oracle.pls_webauth.login; ',
'    END IF;',
'  ',
'end; ',
'',
'function do_authenticate',
'    (p_username varchar2 default null, ',
'    p_password varchar2 default null) ',
'    return boolean is',
'',
'begin',
'--APEX_DEBUG.ENABLE (9);',
'--apex_debug.message(''In do_authenticate the value of APP_USER= %s'', :APP_USER);',
'',
'    -- We only get here if the sentry function call to WebAuth returned false',
'    -- And the user filled in the username/password on page 101',
'    -- Attempt to Authenticate with LDAP with the username and password from page 101',
'--    :P1_IP_ADDRESS := owa_util.get_cgi_env(''REMOTE_ADDR'');',
'--    :P1_AUTHENTICATION_TYPE := ''LDAP'';',
'',
'    return oracle.slac_tools.authenticate_aduser(lower(p_username),p_password);',
'  ',
'end; '))
,p_invalid_session_type=>'LOGIN'
,p_use_secure_cookie_yn=>'N'
,p_ras_mode=>0
);
end;
