,p_attribute_11=>'Y'
,p_attribute_14=>'STD'
,p_plsql_code=>wwv_flow_string.join(wwv_flow_t_varchar2(
'procedure post_auth_26316821105064101 is',
'begin',
'',
'begin',
' ',
'   apex_util.set_session_state(p_name  => ''FSP_AFTER_LOGIN_URL''',
'      ,p_value => ''f?'' || OWA_UTIL.GET_CGI_ENV(''QUERY_STRING''));',
'      ',
'end; ',
'',
'begin',
'          :P0_CATER_USER := null;',
'',
'  select max(but_sid) into :P0_PERSON_ID',
'  from but',
'  where but_kid = upper(v(''APP_USER''));',
'',
'exception',
'  when no_data_found then',
'    :P0_PERSON_ID := null;',
'end;',
'',
'',
'begin',
'',
'  select div_code_id into :P0_SLAC_DIVISION',
'  from art_users',
'  where person_id = :P0_PERSON_ID;',
'',
'exception',
'  when others then',
'    :P0_SLAC_DIVISION := 1;',
'end;',
'',
'',
'begin',
'',
'  select name, maildisp into :P0_MYNAME, :P0_MYEMAIL',
'  from person',
'  where key = :P0_PERSON_ID;',
'',
'exception ',
'  when others then ',
'    :P0_MYNAME := ''Unknown'';',
'    :P0_MYEMAIL := null; ',
'end;',
'',
'',
'begin',
'',
'  :P0_USER_ROLE_LEVEL := null;',
'',
'-- Poonam - 5/10/16 - Added P0_ALLOWED_CODES  so user can view All Divisions in read-only mode or if not a valid Cater user',
'  begin',
'    select eoic_chk',
'          ,admin_chk',
'          ,super_admin_chk',
'          ,hw_closer_chk',
'          ,sw_closer_chk',
'         ,allowed_codes',
'    into :P0_EOIC',
'        ,:P0_ADMIN',
'        ,:P0_SUPER_ADMIN',
'        ,:P0_HW_CLOSER',
'        ,:P0_SW_CLOSER',
'        ,:P0_ALLOWED_CODES',
'    from art_users',
'    where person_id = nvl(:P0_PERSON_ID,0);',
'',
'  begin',
'    select ''Y'' into :P0_AREA_MANAGER',
'    from art_junc_area_person',
'    where person_id = :P0_PERSON_ID',
'    and rownum < 2;',
'  exception',
'    when no_data_found then :P0_AREA_MANAGER := ''N'';',
'  end;',
'',
'    insert into art_user_log',
'    (username',
'    ,user_sid',
'    ,superadmin',
'    ,admin',
'    ,eoic',
'    ,area_manager',
'    )',
'    values',
'--    (:P101_USERNAME',
'    (lower(v(''APP_USER''))',
'    ,:P0_PERSON_ID',
'    ,:P0_SUPER_ADMIN',
'    ,:P0_ADMIN',
'    ,:P0_EOIC',
'    ,:P0_AREA_MANAGER',
'    );',
'',
'  exception',
'    when no_data_found then',
'      :P0_ADMIN           := null;',
'      :P0_EOIC            := null;',
'      :P0_SUPER_ADMIN     := null;',
'      :P0_USER_ROLE_LEVEL := 1;',
'      :P0_CATER_USER := ''N'';',
'',
'  end;',
'',
'  if  :P0_USER_ROLE_LEVEL is null',
'  then',
'    select max(ur.relative_value)',
'    into :P0_USER_ROLE_LEVEL',
'    from art_junc_div_userrole_user uur',
'        ,art_user_roles             ur',
'    where uur.user_role_id = ur.user_role_id',
'    and   uur.user_id      = :P0_PERSON_ID;',
'  end if;',
'',
'  if :P0_EOIC = ''Y''',
'  then',
'    :P0_USER_ROLE_LEVEL := 6;',
'  end if;',
'',
'  if :P0_ADMIN = ''Y''',
'  then',
'    :P0_USER_ROLE_LEVEL := 7;',
'  end if;',
'',
'  if :P0_SUPER_ADMIN = ''Y''',
'  then',
'    :P0_USER_ROLE_LEVEL := 8;',
'  end if;',
'',
'  if :P0_SUPER_ADMIN is null',
'  then',
'    :P0_USER_ROLE_LEVEL := 1;',
'  end if;',
'',
'exception',
'  when no_data_found then',
'    :P0_ADMIN           := null;',
'    :P0_EOIC            := null;',
'    :P0_SUPER_ADMIN     := null;',
'    :P0_USER_ROLE_LEVEL := 1;',
'-- Poonam - 5/10/16 - Added P0_ALLOWED_CODES  so user can view All Divisions in read-only mode or if not a valid Cater user',
'    :P0_ALLOWED_CODES := ''3:2:1'';',
'end;',
'',
'end;'))
,p_invalid_session_type=>'LOGIN'
,p_logout_url=>'f?p=&APP_ID.:102'
,p_post_auth_process=>'post_auth_26316821105064101'
,p_cookie_name=>'LOGIN_USERNAME_COOKIE'
,p_use_secure_cookie_yn=>'N'
,p_ras_mode=>0
);
end;
