 p_id=>wwv_flow_api.id(486818550779501418)
,p_name=>'Radiation Safety Work Control Forms'
,p_parent_plug_id=>wwv_flow_api.id(539336063995807628)
,p_template=>wwv_flow_api.id(506058266482964176)
,p_display_sequence=>4
,p_region_template_options=>'#DEFAULT#'
,p_component_template_options=>'#DEFAULT#'
,p_new_grid_row=>false
,p_new_grid_column=>false
,p_display_point=>'BODY'
,p_source_type=>'NATIVE_SQL_REPORT'
,p_query_type=>'FUNC_BODY_RETURNING_SQL'
,p_source=>wwv_flow_string.join(wwv_flow_t_varchar2(
'declare',
'',
'  l_div_code_id   varchar2(10)   := :P1_DIV_CODE_ID;',
'  l_pps_zone_id   varchar2(10)   := :P1_PPS_ZONE;',
'  l_form_status   varchar2(10)   := :P1_FORM_STATUS;',
'  l_search_string varchar2(1000) := :P1_REPORT_SEARCH;',
'  l_from_date     varchar2(20)   := :P1_FROM_DATE;',
'  l_to_date       varchar2(20)   := :P1_TO_DATE;',
'  l_person_resp   varchar2(10)   := :P1_PERSON_RESP;',
'  l_eoic          varchar2(10)   := :P1_EOIC;',
'  l_rad_physics   varchar2(10)   := :P1_RAD_PHYSICS;',
'  l_area_mgr      varchar2(10)   := :P1_AREA_MGR;',
'  l_area          varchar2(10)   := :P1_AREA;',
'  l_group         varchar2(10)   := :P1_GROUP;',
'  l_bas_list      varchar2(10)   := :P1_BAS_LIST;',
'  ',
'  l_sql     varchar2(4000);',
'  l_labela  varchar2(100) := ''Open for less than 1 day'';',
'  l_labelb  varchar2(100) := ''Open for more than 1 day but less than a week'';',
'  l_labelc  varchar2(100) := ''Open for more than a week'';',
'  ',
'begin',
'  ',
'  l_sql := ''',
'  select case',
'         when days_open             < 1 then '''''' || l_labela || ''''''',
'         when days_open between 1 and 7 then '''''' || l_labelb || ''''''',
'         else                                '''''' || l_labelc || ''''''',
'         end days_open_range',
'        ,form_id',
'        ,form_id_display',
'        ,prob_id',
'        ,status',
'        ,days_open',
'        ,modified_date',
'        ,created_date',
'        ,task_person',
'        ,description',
'        ,area',
'        ,group_name',
'        ,div_code',
'        ,ppszone',
'        ,number_of_bas',
'  from (select distinct f.form_id       form_id',
'                       ,f.form_id       form_id_display',
'                       ,f.prob_id       prob_id',
'                       ,fs.status       status',
'                       ,round((sysdate - f.created_date),1) days_open',
'                       ,f.modified_date modified_date',
'                       ,f.created_date  created_date',
'                       ,p.name          task_person',
'                       ,f.s1_descr      description',
'                       ,a.area          area',
'                       ,g.name          group_name',
'                       ,dc.div_code     div_code',
'                       ,pz.ppszone      ppszone',
'                       ,(select count(*) from rsw_junc_form_bas b where b.form_id = f.form_id and b.bas_id is not null) number_of_bas',
'  from rsw_form           f',
'      ,rsw_form_status    fs',
'      ,person             p',
'      ,art_areas          a',
'      ,art_group          g',
'      ,rsw_junc_form_bas  b',
'      ,art_problems       p',
'      ,art_division_codes dc',
'      ,art_ppszones       pz',
'  where f.form_status_id    = fs.form_status_id(+)',
'  and   f.s1_task_person_id = p.key            (+)',
'  and   f.s1_area_id        = a.area_id        (+)',
'  and   f.s1_group_id       = g.group_id       (+)',
'  and   f.form_id           = b.form_id        (+)',
'  and   f.prob_id           = p.prob_id',
'  and   p.div_code_id       = dc.div_code_id',
'  and   f.s1_pps_zone_id    = pz.ppszone_id    (+)',
'  and   instr('''''' || l_div_code_id || '''''',p.div_code_id) > 0',
''';',
'',
'  if  l_form_status is not null',
'  then',
'    case l_form_status',
'    when ''100'' then l_sql := l_sql || '' and f.form_status_id != 6 and f.form_status_id != 7'';',
'    else l_sql := l_sql || '' and f.form_status_id = to_number('''''' || l_form_status || '''''')'';',
'    end case;',
'  end if;',
'',
'  if l_search_string is not null',
'  then',
'      l_sql := l_sql || '' and upper(f.s1_descr) like ''''%''|| upper(l_search_string) || ''%'''''';',
'  end if;',
'',
'  if  l_from_date is not null',
'  -- and l_to_date   is not null',
'  then',
'     l_sql := l_sql || '' and (f.created_date between to_date('''''' || l_from_date || '''''',''''mm/dd/yyyy hh24:mi'''') and to_date('''''' || l_to_date || '''''',''''mm/dd/yyyy hh24:mi'''') or f.modified_date between to_date('''''' || l_from_date || '''''',''''mm/dd/yyyy hh24:'
||'mi'''') and to_date(nvl('''''' || l_to_date || '''''',to_char(sysdate,''''mm/dd/yyyy hh24:mi'''')),''''mm/dd/yyyy hh24:mi''''))'';',
'  end if;',
'',
'  if  l_person_resp is not null',
'  then',
'      l_sql := l_sql || '' and f.s1_task_person_id = to_number('''''' || l_person_resp || '''''')'';',
'  end if;',
'',
'  if  l_eoic is not null',
'  then',
'      l_sql := l_sql || '' and f.s2_eoic_id = to_number('''''' || l_eoic || '''''')'';',
'  end if;',
'',
'  if  l_rad_physics is not null',
'  then',
'      l_sql := l_sql || '' and f.s2_rad_id = to_number('''''' || l_rad_physics || '''''')'';',
'  end if;',
'',
'  if  l_pps_zone_id is not null',
'  and l_pps_zone_id != 41',
'  and l_pps_zone_id != 42',
'  then',
'      l_sql := l_sql || '' and f.s1_pps_zone_id = to_number('''''' || l_pps_zone_id || '''''')'';',
'  end if;',
'',
'  if  l_area_mgr is not null',
'  then',
'      l_sql := l_sql || '' and f.s1_area_mgr_id = to_number('''''' || l_area_mgr || '''''')'';',
'  end if;',
'',
'  if  l_area is not null',
'  then',
'      l_sql := l_sql || '' and f.s1_area_id = to_number('''''' || l_area || '''''')'';',
'  end if;',
'',
'  if  l_group is not null',
'  then',
'      l_sql := l_sql || '' and f.s1_group_id = to_number('''''' || l_group || '''''')'';',
'  end if;',
'',
'  if  l_bas_list is not null',
'  then',
'      l_sql := l_sql || '' and instr('''''' || l_bas_list || '''''',b.bas_id) > 0 '';',
'  end if;',
'',
'  l_sql := l_sql || '' order by days_open asc)'';',
'',
'  --dbms_output.put_line(l_sql);',
'  return l_sql;',
'',
'end;',
''))
,p_ajax_enabled=>'Y'
