'declare ',
'temp varchar2(4000);',
'',
'cursor getit is',
'select display_order, type,',
'   text, work_reqs, worker_text, ',
'   to_char(last_wr_date,''mm/dd/yy hh24:mi'') last_wr_date, ',
'   initcap(last_wr_by) last_wr_by,',
'   to_char(last_wt_date,''mm/dd/yy hh24:mi'') last_wt_date, ',
'   initcap(last_wt_by) last_wt_by',
'from (',
'select ',
'  a.display_order,',
'  a.code type,',
'     a.text,',
'     htmldb_item.hidden(1,a.check_id) ||',
'     htmldb_item.textarea(2,null,2,60,:P12_ADSO_FORM_ATTRIB) work_reqs,',
'     htmldb_item.textarea(3,null,2,60,:P12_USER_FORM_ATTRIB) worker_text,',
'     null last_wr_date,',
'     null last_wr_by,',
'     null last_wt_date,',
'     null last_wt_by',
'from RSW_checklist a',
'where a.check_id not in ',
'  (select check_id from RSW_junc_form_check ',
'   where form_id = :P12_FORM_ID)',
'union',
'select ',
'  a.display_order,',
'  a.code type,',
'      a.text,',
'      htmldb_item.hidden(1,a.check_id) ||',
'     htmldb_item.textarea(2,b.work_requirements,2,60,:P12_ADSO_FORM_ATTRIB) work_req,',
