'        if c1.last_wt_date is not null then ',
'           htp.p(''<td>Last Update:'' || c1.last_wt_date || '' By: '' || ',
'             c1.last_wt_by || ''</td>'');',
'        else',
'           htp.p(''<tr><td>&nbsp;</td>''); ',
'        end if; ',
'     end if; ',
'  end loop;',
'  htp.p(''</table>'');',
'end; ',
'',
'',
' ',
''))
