'         if length(new_worker_text) < 20',
'         then',
'           new_worker_text := null;',
'         end if; ',
'       exception when others then ',
'         new_worker_text := null; ',
'       end;',
'',
'       if found = ''Y'' then',
'',
'          update RSW_junc_form_check ',
'             set work_requirements = new_work_reqs',
'                ,worker_text       = new_worker_text',
'                ,trans_id          = :P12_TRANS_ID',
'          where',
